/* REMEMBER TO REPLACE FOREIGN KEYS WITH YOUR OWN GENERATED FOREIGN KEYS*/
CREATE DATABASE conversation_db;
CREATE TABLE restaurant(
restaurant_uid uuid DEFAULT uuid_generate_v4() PRIMARY KEY,
restaurant_name VARCHAR(255) NOT NULL
);

CREATE TABLE menu(
restaurant_uid uuid REFERENCES restaurant(restaurant_uid) NOT NULL,
menu_uid uuid DEFAULT uuid_generate_v4() PRIMARY KEY,
menu_name VARCHAR(255) NOT NULL
);


CREATE TABLE menu_items(
  restaurant_uid uuid REFERENCES restaurant(restaurant_uid) NOT NULL,
  item_uid uuid DEFAULT uuid_generate_v4() PRIMARY KEY,
  menu_uid uuid REFERENCES menu(menu_uid) NOT NULL,
  name VARCHAR(50) NOT NULL,
  price numeric NOT NULL,
  img VARCHAR(500) NOT NULL,
  description TEXT,
  ingredients TEXT,
  allergen_1 VARCHAR(50),
  allergen_2 VARCHAR(50), 
  allergen_3 VARCHAR(50), 
  allergen_4 VARCHAR(50), 
  allergen_5 VARCHAR(50)   
);

